// The inner coroutine is launched independently and inherits
// the CoroutineContext from the parent (Dispatchers.IO)
val scope = CoroutineScope(Dispatchers.IO)
scope.launch {
    launch {
    }
}

launch(Dispatchers.Unconfined) {
    println("Started on thread: ${Thread.currentThread().name}")
    delay(1000) // Suspension point
    println("Resumed on thread: ${Thread.currentThread().name}")
}

// Explicitly job
val explicitlyJob = Job()
val scope = CoroutineScope(Dispatchers.IO + explicitlyJob)

val implicitlyJob = scope.launch { ... }

explicitlyJob.cancel()

// Implicitly job
val implicitlyJob = CoroutineScope(Dispatchers.IO).launch { ... }
implicitlyJob.cancel()

val handler =
    CoroutineExceptionHandler { coroutineContext, exception ->
        ...
    }
val scope = CoroutineScope(handler)
scope.launch(handler) {
    throw RuntimeException("Exception message!!!")
}

val name = CoroutineName("Test")
val thread = Dispatchers.Main
CoroutineScope(thread + name).launch {
    Log.i(TAG, Thread.currentThread().name)
}
