suspend fun task1() {
    delay(2_000)
} 

suspend fun task2() {
    delay(500)
}

suspend fun task3() {
    delay(1_000)
    throw RuntimeException("Exception in the task3")
}

val exceptionHandler = CoroutineExceptionHandler { coroutineContext, throwable ->
    // Do something
}
val supervisorJob = Job()
val scope = CoroutineScope(Dispatchers.Main + supervisorJob + exceptionHandler)
scope.launch {
    task1()
}
scope.launch {
    task2()
}
scope.launch {
    task3()
}

val handler = CoroutineExceptionHandler { coroutineContext, throwable ->
    // Do something
}

supervisorScope {
    launch {
        delay(2000)
    }
    launch(handler) {
        delay(1000)
        throw RuntimeException("Thrown exception!!!")
    }
}
