interface Employer {

    val travelExpenses: Long

    var salary: Long
        get() = MINIMUM_SALARY +
                travelExpenses -
                calculateInsurance()
        set(value) {
            MINIMUM_SALARY
        }

    companion object {
        const val MINIMUM_SALARY = 12_000_000L
    }

    fun calculateInsurance(): Long {
        return MINIMUM_SALARY * 10 / 100
    }

    fun work()
}
